INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'ro_RO', 'Te rugăm să validezi contul pe {WEB_TITLE}', '<p>Bună {USER_NAME},</p><p>Te rugăm să confirmi înregistrarea dând click pe: {VALIDATION_LINK}</p><p>Mulțumim!</p><p>Echipa,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'ro_RO', '{WEB_TITLE} - Înregistrare cu succes!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'ro_RO', 'Uite ce am găsit pe {WEB_TITLE}', '<p>Bună {FRIEND_NAME},</p><p>Prietenul tău {USER_NAME} vrea să îți arate acest anunț <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Mesaj:</p><p>{COMMENT}</p><p>Echipa,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'ro_RO', '{WEB_TITLE} - Anunț nou in the last hour', '<p>Bună {USER_NAME},</p><p>Au fost publicate anunțuri noi în ultima oră. Le poți vedea aici:</p><p>{ADS}</p><p>-------------</p><p>Pentru a te dezabona de la această alertă, click pe: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'ro_RO', '{WEB_TITLE} - Anunț nou in the last day', '<p>Bună {USER_NAME},</p><p> Astăzi au fost publicate anunțuri noi. Le poți vedea aici:</p><p>{ADS}</p><p>-------------</p><p> Pentru a te dezabona de la această alertă, click pe: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'ro_RO', '{WEB_TITLE} - Anunț nou in the last week', '<p>Bună {USER_NAME},</p><p>Au fost publicate anunțuri noi în ultima săptămână. Le poți vedea aici :</p><p>{ADS}</p><p>-------------</p><p>Pentru a te dezabona de la această alertă, click pe: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'ro_RO', '{WEB_TITLE} - Anunț nou', '<p>Bună {USER_NAME},</p><p>A fost publicat un anunț nou!</p><p>{ADS}</p><p>-------------</p><p>Pentru a te dezabona de la această alertă, click pe: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'ro_RO', '{WEB_TITLE} - Comentariu nou', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'ro_RO', '{WEB_TITLE} - Opțiuni de editare pentru anunțul {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'ro_RO', '{WEB_TITLE} - Validează anunțul', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'ro_RO', '{WEB_TITLE} - A fost publicat un anunț nou', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'ro_RO', '{WEB_TITLE} - Recuperare parolă', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'ro_RO', '{WEB_TITLE} - Ai solicitat modificarea adresei de e-mail', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'ro_RO', '{WEB_TITLE} - Te rugăm să validezi alerta', '<p>Bună {USER_NAME},</p><p>Te rugăm să confirmi înregistrarea dând click pe următorul link: {VALIDATION_LINK}</p><p>Mulțumim!</p><p>Echipa,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'ro_RO', '{WEB_TITLE} - Comentariul tău a fost aprobat', ' 	<p>Buna{COMMENT_AUTHOR},</p><p>Comentariul dvs.  <a href="{ITEM_URL}">{ITEM_TITLE}</a> a fost aprobat.</p><p>Cu stima,</p><p>{WEB_LINK}</p> ');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'ro_RO', '{WEB_TITLE} - Validează anunțul', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'ro_RO', '{WEB_TITLE} - S-a înregistrat un utilizator nou', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'ro_RO', '{WEB_TITLE} - Un utilizator are o întrebare pentru tine', '<p>Bună {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) ți-a lăsat un mesaj:</p><p>{COMMENT}</p><p>Echipa,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'ro_RO', '{WEB_TITLE} - Cineva a adăugat un comentariu la anunțul tău', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'ro_RO', '{WEB_TITLE} - Crearea contului de admin a fost cu succes!', '<p>Salutare {ADMIN_NAME},</p><p>Administratorul site-ului {WEB_LINK} a creat un cont pentru tine,</p><ul><li>Nume utilizator: {USERNAME}</li><li>Parolă: {PASSWORD}</li></ul><p>Poţi accesa panoul de administrare aici: {WEB_ADMIN_LINK}.</p><p>Îţi mulţumim!</p><p>Să auzim numai de bine,</p>');
