INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (1, 'ru_RU', '{WEB_TITLE} - У кого-то есть вопрос о вашем объявлении', '<p>Привет {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) написал сообщение по поводу Вашего объявления <a href="{ITEM_URL}">{ITEM_TITLE}</a>:</p><p>{COMMENT}</p><p>С уважением,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (2, 'ru_RU', 'Подтверждение регистрации на сайте {WEB_TITLE}', '<p>Привет {USER_NAME},</p><p>Просьба подтвердить Вашу регистрацию, нажав на следующую ссылку: {VALIDATION_LINK}</p><p>Спасибо!</p><p>С уважением,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (3, 'ru_RU', '{WEB_TITLE} - Успешная регистрация!', '<p>Hi {USER_NAME},</p><p>You\'ve successfully registered for {WEB_LINK}.</p><p>Thank you!</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (4, 'ru_RU', 'Посмотри, что я обнаружил на {WEB_TITLE}', '<p>Привет {FRIEND_NAME},</p><p>Ваш друг {USER_NAME} хочет поделиться с Вами информацией <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Сообщение:</p><p>{COMMENT}</p><p>С уважением,</p><p>{WEB_TITLE}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (5, 'ru_RU', '{WEB_TITLE} - Новое объявление in the last hour', '<p>Привет {USER_NAME},</p><p>Новые объявления опубликованные за последний час. Взгляните на них:</p><p>{ADS}</p><p>-------------</p><p>Чтобы отписаться от этой рассылки , перейдите по ссылке: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (6, 'ru_RU', '{WEB_TITLE} - Новое объявление in the last day', '<p>Привет {USER_NAME},</p><p>Новые объявления за прошедший день. Взгляните на них:</p><p>{ADS}</p><p>-------------</p><p>Чтобы отписаться от этой рассылки, перейдите по ссылке: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (7, 'ru_RU', '{WEB_TITLE} - Новое объявление in the last week', '<p>Привет {USER_NAME},</p><p>Новые объявления опубликованные на прошлой неделе. Взгляните на них:</p><p>{ADS}</p><p>-------------</p><p>Чтобы отписаться от этой рассылки, перейдите по ссылке: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (8, 'ru_RU', '{WEB_TITLE} - Новое объявление', '<p>Привет {USER_NAME},</p><p>Опубликовано новое объявление!</p><p>{ADS}</p><p>-------------</p><p>Для того чтобы отписаться от этой рассылки, перейдите по ссылке: {UNSUB_LINK}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (9, 'ru_RU', '{WEB_TITLE} - Новый комментарий', '<p>Someone commented on the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Commenter: {COMMENT_AUTHOR}<br />Commenter\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (10, 'ru_RU', '{WEB_TITLE} - Возможности редактирования для объявления {ITEM_TITLE}', '<p>Hi {USER_NAME},</p><p>You\'re not registered at {WEB_LINK}, but you can still edit or delete the listing <a href="{ITEM_URL}">{ITEM_TITLE}</a> for a short period of time.</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>If you register as a user, you will have full access to editing options.</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (11, 'ru_RU', '{WEB_TITLE} - Подтверждение объявления', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because a listing has been published at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this email.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (12, 'ru_RU', '{WEB_TITLE} - Опубликовано новое объявление', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a listing has been published at {WEB_LINK}.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact email: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>You can edit this listing by clicking on the following link: {EDIT_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (13, 'ru_RU', '{WEB_TITLE} - Восстановления пароля', '<p>Hi {USER_NAME},</p><p>We\'ve sent you this e-mail because you\'ve requested a password reminder. Follow this link to recover it: {PASSWORD_LINK}</p><p>The link will be deactivated in 24 hours.</p><p>If you didn\'t request a password reminder, please ignore this message. This request was made from IP {IP_ADDRESS} on {DATE_TIME}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (14, 'ru_RU', '{WEB_TITLE} - Запрос на смену адреса почты', '<p>Hi {USER_NAME}</p><p>You\'re receiving this e-mail because you requested an e-mail change. Please confirm this new e-mail address by clicking on the following validation link: {VALIDATION_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (15, 'ru_RU', '{WEB_TITLE} - Пожалуйста, подтвердите подписку', '<p>Привет {USER_NAME},</p><p> подтвердите регистрацию, нажав на следующую ссылку: {VALIDATION_LINK}</p><p>Спасибо!</p><p>С уважением,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (16, 'ru_RU', '{WEB_TITLE} - Ваш комментарий одобрен', '<p>Привет {COMMENT_AUTHOR},</p><p>Ваш комментарий на <a href="{ITEM_URL}">{ITEM_TITLE}</a> успешно утвержден.</p><p>С уважением,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (17, 'ru_RU', '{WEB_TITLE} - Подтверждение объявления', '<p>Hi {USER_NAME},</p><p>You\'re receiving this e-mail because you\’ve published a listing at {WEB_LINK}. Please validate this listing by clicking on the following link: {VALIDATION_LINK}. If you didn\'t publish this listing, please ignore this e-mail.</p><p>Listing details:</p><p>Contact name: {USER_NAME}<br />Contact e-mail: {USER_EMAIL}</p><p>{ITEM_DESCRIPTION}</p><p>Url: {ITEM_URL}</p><p>Even if you\'re not registered at {WEB_LINK}, you can still edit or delete your listing:</p><p>You can edit your listing by following this link: {EDIT_LINK}</p><p>You can delete your listing by following this link: {DELETE_LINK}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (18, 'ru_RU', '{WEB_TITLE} - Регистрация нового пользователя', '<p>Dear {WEB_TITLE} admin,</p><p>You\'re receiving this email because a new user has been created at {WEB_LINK}.</p><p>User details:</p><p>Name: {USER_NAME}<br />E-mail: {USER_EMAIL}</p><p>Regards,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (19, 'ru_RU', '{WEB_TITLE} - У кого то есть вопрос к Вам', '<p>Привет {CONTACT_NAME}!</p><p>{USER_NAME} ({USER_EMAIL}, {USER_PHONE}) оставил Вам сообщение:</p><p>{COMMENT}</p><p>С уважением,</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (20, 'ru_RU', '{WEB_TITLE} - Кто то оставил комментарий на Ваше объявление', '<p>There\'s a new comment on the listing: <a href="{ITEM_URL}">{ITEM_TITLE}</a>.</p><p>Author: {COMMENT_AUTHOR}<br />Author\'s email: {COMMENT_EMAIL}<br />Title: {COMMENT_TITLE}<br />Comment: {COMMENT_TEXT}</p><p>{WEB_LINK}</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (21, 'ru_RU', '{WEB_TITLE} - Успешное создание учетной записи администратора!', '<p>Здравствуйте {ADMIN_NAME},</p><p>Администратор {WEB_LINK} создал для Вас личный кабинет,</p><ul><li>Имя пользователя: {USERNAME}</li><li>Пароль: {PASSWORD}</li></ul><p>Для управления вашим личным кабинетом перейдите по ссылке {WEB_ADMIN_LINK}.</p><p>Благодарим Вас!</p><p>С уважением,</p>');
INSERT INTO /*TABLE_PREFIX*/t_pages_description (fk_i_pages_id, fk_c_locale_code, s_title, s_text) VALUES (22, 'ru_RU', '{WEB_TITLE} - Ваше объявление скоро станет не актуальным', '<p>Привет {USER_NAME},</p><p> Ваше объявление <a href="{ITEM_URL}">{ITEM_TITLE}</a> - уже не актуально {WEB_LINK}.');
